const MAX_LINES = 6;
let currentLines = [];
let isAnimating = false;
let isFirstConsult = true;
let currentQuestion = null;

const turtleShell = document.getElementById('turtleShell');
const lineDisplay = document.getElementById('lineDisplay');
const resultContent = document.getElementById('resultContent');
const resultPanel = document.getElementById('resultPanel');
const subTitle = document.getElementById('subTitle');
const resetBtn = document.getElementById('resetBtn');
const singleCoin = document.getElementById('singleCoin');

// Helper function to create a single yao line element
function createYaoElement(type) {
    const yaoEl = document.createElement('div');
    yaoEl.classList.add('yao');
    if (type === 'solid') {
        const span = document.createElement('span');
        span.classList.add('yao-solid');
        yaoEl.appendChild(span);
    } else { // broken
        const span1 = document.createElement('span');
        const span2 = document.createElement('span');
        yaoEl.classList.add('yao-broken');
        yaoEl.appendChild(span1);
        yaoEl.appendChild(span2);
    }
    return yaoEl;
}

// Helper to determine coin result and line type
function castCoins() {
    let heads = 0; // 阳
    let tails = 0; // 阴
    for (let i = 0; i < 3; i++) {
        if (Math.random() > 0.5) {
            heads++;
        } else {
            tails++;
        }
    }

    if (heads === 3) return { value: 9, type: 'solid', char: 'O', name: '老阳' };      // 老阳
    if (tails === 3) return { value: 6, type: 'broken', char: 'X', name: '老阴' };      // 老阴
    if (heads === 2) return { value: 8, type: 'broken', char: '--', name: '少阴' };     // 少阴
    if (tails === 2) return { value: 7, type: 'solid', char: '—', name: '少阳' };       // 少阳
}

// Animation for casting coins
function animateCast(callback) {
    isAnimating = true;
    turtleShell.classList.add('shaking');
    singleCoin.classList.add('visible');

    const result = castCoins();
    const resultChar = result.char === '--' ? '阴' : '阳';
    singleCoin.textContent = resultChar;

    setTimeout(() => {
        turtleShell.classList.remove('shaking');
        singleCoin.classList.remove('visible');
        isAnimating = false;
        callback(result);
    }, 1500); // Shake for 1.5 seconds
}

// Cast a single line and display it
function castOneLine() {
    if (isAnimating) return;

    animateCast((result) => {
        const line = {
            value: result.value,
            type: result.type,
            char: result.char,
            mutable: result.value === 6 || result.value === 9
        };

        const yaoEl = createYaoElement(line.type);
        yaoEl.classList.add('new-yao');
        lineDisplay.prepend(yaoEl);

        setTimeout(() => {
            yaoEl.classList.remove('new-yao');
            yaoEl.classList.add('visible');
        }, 10);

        currentLines.unshift(line);

        if (currentLines.length < MAX_LINES) {
            subTitle.textContent = `第 ${currentLines.length + 1} 次投掷`;
        } else {
            subTitle.textContent = '六爻已成，分析中...';
            turtleShell.style.display = 'none';
            resetBtn.classList.remove('hidden');
            analyzeHexagram();
        }
    });
}

// Get hexagram name from lines
function getHexagramName(lines) {
    const bottomTrigram = lines.slice(0, 3).map(l => l.value);
    const topTrigram = lines.slice(3, 6).map(l => l.value);

    // Simplified mapping for demonstration
    const trigramMap = {
        '7,7,7': '乾', '8,8,8': '坤',
        '7,8,8': '震', '8,7,7': '艮',
        '7,7,8': '兑', '8,8,7': '巽',
        '7,8,7': '坎', '8,7,8': '离'
    };

    const bottomType = bottomTrigram.map(v => v === 7 || v === 9 ? '阳' : '阴').join('');
    const topType = topTrigram.map(v => v === 7 || v === 9 ? '阳' : '阴').join('');
    
    // Reverse the trigramMap lookup
    const simplifiedBottom = bottomTrigram.map(v => v === 7 || v === 9 ? 7 : 8).join(',');
    const simplifiedTop = topTrigram.map(v => v === 7 || v === 9 ? 7 : 8).join(',');
    
    const bottomName = trigramMap[simplifiedBottom];
    const topName = trigramMap[simplifiedTop];

    return topName + '为' + bottomName;
}

// Analyze the hexagram and show the result
function analyzeHexagram() {
    resultPanel.classList.add('visible');
    resultContent.innerHTML = '';
    resultContent.classList.add('loading-dots');

    setTimeout(() => {
        resultContent.classList.remove('loading-dots');
        let hexagramName = getHexagramName(currentLines);
        const hexagramData = I_CHING_DATA[hexagramName];

        let resultHTML = '';
        if (hexagramData) {
            resultHTML = `<h2>${hexagramName} 卦</h2>`;
            resultHTML += `<p><strong>卦辞：</strong>${hexagramData['卦辞']}</p>`;
            resultHTML += `<p>${hexagramData['解']}</p>`;
            resultHTML += '<h3>爻辞解析</h3>';

            let mutableYaoFound = false;
            currentLines.forEach((line, index) => {
                if (line.mutable) {
                    mutableYaoFound = true;
                    const yaoData = hexagramData['爻辞'][index];
                    resultHTML += `<p><strong>${yaoData['名']}（动爻）：</strong><br/>`;
                    resultHTML += `&nbsp;&nbsp;&nbsp;&nbsp;${yaoData['辞']}</p>`;
                    resultHTML += `<p class="explanation">&nbsp;&nbsp;&nbsp;&nbsp;<strong>解析：</strong>${yaoData['解']}</p>`;
                }
            });

            if (!mutableYaoFound) {
                // No changing lines, show the whole hexagram explanation
                 resultHTML = `<h2>${hexagramName} 卦</h2>`;
                 resultHTML += `<p><strong>卦辞：</strong>${hexagramData['卦辞']}</p>`;
                 resultHTML += `<p>${hexagramData['解']}</p>`;
                 resultHTML += `<p><strong>所有爻都无变动，以本卦卦辞为主。</strong></p>`;
            }
        } else {
            resultHTML = `<h2>无法找到该卦象的解析。</h2><p>请检查卦象是否正确。</p>`;
        }
        resultContent.innerHTML = resultHTML;
        
    }, 2000);
}

// Reset the divination
function resetDivination() {
    currentLines = [];
    isFirstConsult = false;
    lineDisplay.innerHTML = '';
    resultPanel.classList.remove('visible');
    resetBtn.classList.add('hidden');
    turtleShell.style.display = 'block';
    subTitle.textContent = '心中默念所问之事，点击龟壳开始占卜';
}

turtleShell.addEventListener('click', () => {
    if (currentLines.length < MAX_LINES) {
        castOneLine();
    }
});

resetBtn.addEventListener('click', resetDivination);
